sourcesContent:
import { MongoClient } from 'mongodb';

class DBClient {
  constructor() {
    this.host = process.env.DB_HOST || 'localhost';
    this.port = process.env.DB_PORT || 27017;
    this.database = process.env.DB_DATABASE || 'files_manager';

    const url = `mongodb://${this.host}:${this.port}`;

    this.client = new MongoClient(url, {
      useUnifiedTopology: true,
    });

    this.client.connect()
      .then(() => {
        console.log('DBClient connected to MongoDB');
      })
      .catch((error) => {
        console.error(`DBClient connection error: ${error}`);
      });
  }

  isAlive() {
    return !!this.client && this.client.isConnected();
  }

  async nbUsers() {
    const collection = this.client.db(this.database).collection('users');
    const count = await collection.countDocuments();
    return count;
  }

  async nbFiles() {
    const collection = this.client.db(this.database).collection('files');
    const count = await collection.countDocuments();
    return count;
  }
}

const dbClient = new DBClient();

export default dbClient;

